<?php//Функция проверки статуса пользователя. Получает ИД пользователя и проверяемый статус. Возвращает 1 если пользователь имеет статус и 0, если нет.  function status ($id_user,$n_status,&$prefix)  {	global $prefix;	if (isset ($prefix))	{$base = $prefix;}	$base .= 'user_in_structure';		$query = "SELECT * from $base where id_user = $id_user";	$result = mysql_query($query) or DIE (mysql_error());	$res = 0;	while ($out = mysql_fetch_array($result))		{			if ($n_status == $out["id_structure"])				{					$res = 1; break;				}		}	return $res;  }    function get_menu($id_user)  {	$res = 0;	global $prefix;	//include ("config.php");	if (isset ($prefix))	{$base = $prefix;}	$base .= 'user_in_structure';		$query = "SELECT * from $base where id_user = $id_user";		$result = mysql_query($query) or DIE (mysql_error());	while ($out = mysql_fetch_array($result))		{							if (isset ($prefix))	{$base = $prefix;}					$base .= 'structure';						$query = "SELECT * from $base where id = ".$out["id_structure"];					$result1 = mysql_query($query) or DIE (mysql_error());					$link = mysql_result ($result1,0,"link");					$link_name = mysql_result ($result1,0,"link_name");					echo "<tr><td><a href = '$link'>$link_name</a></td></tr>";		}		  }?>